import React, { Component } from 'react'
import { ListGroup ,ListGroupItem} from 'reactstrap'

export default class CategoriLists extends Component {
    state = {
        categories : [
            {categoryId:1 ,categoryName:"beverages"},
            {categoryId:2 ,categoryName:"condimanted"}
        ]
    };

    changeCategory=category=>{
        this.setState({currentCategory:category.categoryName});
    };

    render() {
        return (
            <div>
                <h3  >{this.props.info.title}</h3>
                <ListGroup>
                    {this.state.categories.map(category=>(
                        <ListGroupItem onClick= {() =>this.props.changeCategory(category)} key= {category.categoryId}>{category.categoryName}</ListGroupItem>
                    ))}
                </ListGroup>
                <h4>{this.props.currentCategory}</h4>
            </div>
        )
    }
}
